import React, { useState,useEffect } from "react";
import "../css/main.css";
import Button from "react-bootstrap/Button";
import act from "../jsfile/main";
import Axios from "axios";
import Articles from "./articles";

const Main = () => {
  const [refreshInterval, setRefreshInterval] = useState(refreshInUrl || 0);
  const [article, setArticle] = useState("");
  const [rating, setRating] = useState("");
  const [pdate, setPDate] = useState("");
  // const [articles, setArticles] = useState([]);

  const fetchMetrics = () => {
    // retrieve and then setData()
  }  
  useEffect(() => {
    if (refreshInterval && refreshInterval > 0) {
      const interval = setInterval(fetchMetrics, refreshInterval);
      return () => clearInterval(interval);
    }
  }, [refreshInterval]);

  const AddArticle = () => {
    Axios.post("http://localhost:5000/addarticle", {
      article: article,
      rating: rating,
      pdate: pdate,
    }).then((response) => {
      console.log(response);
    });
  };
  
  
  // function refreshPage() {
  //   window.location.reload(false);
  // }

  // useEffect(() => {
  //   Axios.get("http://localhost:5000/getarticle").then((response) => {
  //     setArticles(response.data);
  //     console.table(response.data);
  //   });
  // }, []);

  return (
    <>
    
      <h1>Welcome to main page</h1>
      <Button className="add" onClick={act}>
        Add new Article
      </Button>
      <div id="form" className="form">
        <input
          type="text"
          name="article"
          id="article"
          placeholder="Enter Article Name"
          value={article}
          onChange={(e) => setArticle(e.target.value)}
        />
        <input
          type="number"
          name="rating"
          id="rating"
          placeholder="Rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
        <input
          type="date"
          name="publish"
          id="publish"
          placeholder="Publish Date"
          value={pdate}
          onChange={(e) => setPDate(e.target.value)}
        />
        <Button
          type="submit"
          onClick={() => {
            act();
            AddArticle();
            // refreshPage();
            Articles();
          }}
        >
          Add
        </Button>
      </div>
      <Button>Sort on the basis of Date</Button>
      <Button>Sort on the basis of Rating</Button>
    </>
  );
};

export default Main;
