import "../css/main.css";
import Button from 'react-bootstrap/Button';
import act from "../jsfile/main";
import Axios from "axios";

const Main=()=> {
  const [article, setArticle] = useState("");
  const [rating, setRating] = useState("");
  const [pdate, setPDate] = useState("");
  

  const AddArticle = () => {
    Axios.post("http://localhost:5000/addarticle", {
    article: article,
      rating: rating,
      pdate: pdate,
      
    }).then((response) => {
      console.log(response);
    });
  };
      return (
        <>
        <h1>Welcome to main page</h1>
        <Button className="add" onClick={act}>Add new Article</Button>
        <div id="form" className="form">
            <input type="text" name="article" id="article" placeholder="Enter Article Name"/>
            <input type="number" name="rating" id="rating" placeholder="Rating"/>  
            <input type="date" name="publish" id="publish" placeholder="Publish Date" />
            <button type="submit"  onClick={() => {
          act();
          AddArticle();
          
        }}>Add</button> 
            

        </div>
        <Button>Sort on the basis of Date</Button>
        <Button>Sort on the basis of Rating</Button>
        
        </>
      );}
      

export default Main;
