import React,{ useState,useEffect } from "react";
import "../css/main.css";
import Button from 'react-bootstrap/Button';
import act from "../jsfile/main";
import Axios from "axios";

const Main=()=> {
  const [article, setArticle] = useState("");
  const [rating, setRating] = useState("");
  const [pdate, setPDate] = useState("");
  
  
  const AddArticle = () => {
    Axios.post("http://localhost:5000/addarticle", {
    article: article,
      rating: rating,
      pdate: pdate,
      
    }).then((response) => {
      console.log(response);
    });
  };

  const GetArticle = () => {
    Axios.get("http://localhost:5000/getarticle"
   
      
    ).then((response) => {
      console.log(response);
      const {data}= response
      console.log(data)
    });
  };
  useEffect(() => {
        GetArticle();
      }, [])


      return (
        <>
        <h1>Welcome to main page</h1>
        <Button className="add" onClick={act}>Add new Article</Button>
        <div id="form" className="form">
            <input type="text" name="article" id="article" placeholder="Enter Article Name" value={article}
                onChange={(e) => setArticle(e.target.value)}/>
            <input type="number" name="rating" id="rating" placeholder="Rating" value={rating}
                onChange={(e) => setRating(e.target.value)}/>  
            <input type="date" name="publish" id="publish" placeholder="Publish Date" value={pdate}
                onChange={(e) => setPDate(e.target.value)} />
            <button type="submit"  onClick={() => {
          act();
          AddArticle();
          
        }}>Add</button> 
            

        </div>
        <Button>Sort on the basis of Date</Button>
        <Button>Sort on the basis of Rating</Button>
      <div className="article" onChange={GetArticle}>
        <table className="table table-striped table-inverse table-responsive" border="2px" >
          <thead className="thead-inverse">
            <tr>
              <th>Article</th>
              <th>Rating</th>
              <th>Publish Date</th>
            </tr>
            </thead>
            
            <tbody>
              <tr>
                <td scope="row">Srijan</td>
                <td>5</td>
                <td>12/12/12</td>
              </tr>
              
            </tbody>
        </table>
      </div>
        </>
      );}
      

export default Main;
