  //   });
  // };

  // const rsort = () => {
  //   articles.sort((a, b) => {
  //     return new Date(b.rating) - new Date(a.rating);
  //   });
  // };

  function sortResults(prop, asc) {
    articles.sort(function(a, b) {
        if (asc) {
            return (a[prop] > b[prop]) ? 1 : ((a[prop] < b[prop]) ? -1 : 0);
        } else {
            return (b[prop] > a[prop]) ? 1 : ((b[prop] < a[prop]) ? -1 : 0);
        }
    });
    renderResults();
}

  return (
    <>
      <Button onClick={sortResults(date,true)}>Sort on the basis of Date</Button>
      <Button onClick={sortResults(rating,true)}>Sort on the basis of Rating</Button>

      <div className="article">
        <table
          className="table table-striped table-inverse table-responsive"
          border="2px"
        >
          <thead className="thead-inverse">
            <tr>
              <th>Article</th>
              <th>Rating</th>
              <th>Publish Date</th>
            </tr>
          </thead>

          <tbody>
            {articles.map((article) => (
              <>
                <tr>
                  <td>{article.article}</td>
                  <td>{article.rating}</td>
                  <td>{article.pdate.slice(0, 10)}</td>
                </tr>
              </>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Articles;
